export default function FriendRequest() {
    const Stack = createNativeStackNavigator();
    const [userId, setUserId] = useState("");
    const [friendRequests, setFriendRequests] = useState([]);
    const [friendRequestSendIds, setFriendRequestSendIds] = useState([]);
    useEffect(() => {
        const fetchUser = async () => {
            const userString = await AsyncStorage.getItem("auth");
            const user = JSON.parse(userString);
            const userId = user._id;
            setUserId(userId);
        };
        fetchUser();
        if (userId)
            fetchFriendRequest();
    }, []);
    const fetchFriendRequest = async () => {
        try {
            const response = await axios.get(`http://${ipAddress}:3000/users/${userId}/friendRequests`);
            const friendInitialSort = response.data.friendRequests.sort((a, b) => {
                const nameA = a.name?.toUpperCase();
                const nameB = b.name?.toUpperCase();
                if (nameA < nameB) {
                    return -1;
                }
                if (nameA > nameB) {
                    return 1;
                }
                return 0;
            });
            setFriendRequests(groupByNameInitial(friendInitialSort));
            // setFriends(response.data.friends)
        } catch (error) {
            console.error('Error fetching friendRequests:', error);
        }

    };
    const groupByNameInitial = (dataList) => {
        const groups = new Map();
        dataList.forEach((item) => {
            const initial = item.name.charAt(0).toUpperCase();
            if (!groups.has(initial)) {
                groups.set(initial, []);
            }
            groups.get(initial).push(item);
        });
        return Array.from(groups.values());
    };
    console.log('frient request', friendRequests);
    console.log(`http://${ipAddress}:3000/users/${userId}/friendRequests`);
    return (
        <Stack.Navigator>
            <Stack.Screen name="FriendRequest" component={GetPhoneBookOrRecently} options={{ headerShown: false }} userId={userId} />
        </Stack.Navigator>
    );
}

